function ajaxdiv(url){
	　　　　$.ajax({
　　　　　　type : "get",
　　　　　　async : false,
　　　　　　url : url,
　　　　　　data : '{type:1}',
　　　　　　timeout : 0,
			beforeSend:function(){
				$("#ajaxdiv").hide().fadeTo(300 , 0);
			},
　　　　　  success : function(page){
　　　　　　　　$("#ajaxdiv").html(page);
				$("#ajaxdiv").show().fadeTo(300 , 1);
　　　　　　},
　　　　　　error : function(){
　　　　　　　　console.log("讀取失敗");
　　　　　　}
　　　　});
　　}
